export const log = (debug, ...args) => {
    if (debug)
        console.log(...args);
};
export const debug = (debug, ...args) => {
    if (debug)
        console.debug(...args);
};
export const warn = (debug, ...args) => {
    if (debug)
        console.warn(...args);
};
export const error = (debug, ...args) => {
    if (debug)
        console.error(...args);
};
